import { Dimensions } from 'react-native';
import Constants from 'expo-constants';

const width = Dimensions.get('window').width;
const height = Dimensions.get('window').height;

/* Default Settings*/
let _settingsTabNav = {
    AppTabsNavigatorHeight : 70,
    AppTabsScreenTop : 10,
    AppTabsScreenImageWidth : 30,
    AppTabsScreenImageHeight : 30,
    AppTabsScreenTextSize : 16,
    AppTabsScreenTosaleTop : -5,
    AppTabsScreenTosaleBorderRadius : 45,
    AppTabsScreenTosaleWidth : 90,
    AppTabsScreenTosaleHeight : 90,
    AppTabsScreenTosaleIconSize : 60,
    FontSizeTextPlayer : 14,
    StatusBarHeight : Constants.statusBarHeight

}

if(width < 375)
{
    _settingsTabNav = {
        AppTabsNavigatorHeight : 50,
        AppTabsScreenTop : 5,
        AppTabsScreenImageWidth : 20,
        AppTabsScreenImageHeight : 20,
        AppTabsScreenTextSize : 12,
        AppTabsScreenTosaleTop : -5,
        AppTabsScreenTosaleBorderRadius : 35,
        AppTabsScreenTosaleWidth : 70,
        AppTabsScreenTosaleHeight : 70,
        AppTabsScreenTosaleIconSize : 30,
        FontSizeTextPlayer : 9,
        StatusBarHeight : Constants.statusBarHeight
    }

}
export default {
  window: {
    width,
    height,
  },
  isSmallDevice: width < 375,
  t:true,
  settingsTabNav:_settingsTabNav
};
